<script>
document.addEventListener("DOMContentLoaded", function () {
    const filterBtn = document.querySelector(".product-filters__filter-btn");
    const modal = document.querySelector(".filter-popup");

    if (filterBtn && modal) {
        filterBtn.addEventListener("click", function () {
            modal.classList.add("filter-popup--open");
            document.body.classList.add("mobile-nav");
        });
    }

    const closeBtn = document.querySelector(".filter-popup-close-btn");

    if (closeBtn) {
        closeBtn.addEventListener("click", function () {
            modal.classList.remove("filter-popup--open");
            document.body.classList.remove("mobile-nav");

        });
    }

    var ajaxurl = "<?php echo esc_url(admin_url('admin-ajax.php')); ?>";

    jQuery(document).ready(function ($) {
        // Обробка фільтрації при сабміті
        $('#product-filter-form').on('submit', function (e) {
            e.preventDefault();

            var selectedColor = $('input[name="color"]:checked').val();
            var selectedBrand = $('input[name="brand"]:checked').val();
            var selectedCategory = $('input[name="category"]:checked').val();
            var selectedSize = $('input[name="size"]:checked').val();

            $.ajax({
                type: 'POST',
                url: ajaxurl,
                data: {
                    action: 'filter_products',
                    color: selectedColor,
                    brand: selectedBrand,
                    category: selectedCategory,
                    size: selectedSize,
                },
                beforeSend: function () {
                    $('.swap-products').html('<p>טוען נתונים...</p>');
                },
                success: function (response) {
                    $('.swap-products').html(response);
                    $('.filter-popup').removeClass('filter-popup--open');
                },
            });
        });

        // Обробка кнопки "נקה הכל"
        $('#clear-filters').on('click', function () {
            // Зняти всі галочки
            $('#product-filter-form input[type="checkbox"]').prop('checked', false);

            // Надіслати порожній AJAX-запит для отримання всіх продуктів
            $.ajax({
                type: 'POST',
                url: ajaxurl,
                data: {
                    action: 'filter_products'
                },
                beforeSend: function () {
                    $('.swap-products').html('<p>טוען נתונים...</p>');
                },
                success: function (response) {
                    $('.swap-products').html(response);
                    $('.filter-popup').removeClass('filter-popup--open');
                }
            });
        });
    });
});
</script>



